mapper.update:
UPDATE POSTING_OFF_RESPONSE
SET STATUS_ID = #{status_id}
WHERE ID = #{id}

UPDATE POSTING_APP_RESPONSE
SET STATUS_ID = #{status_id}
WHERE ID = #{id}

mapper.select:
SELECT P_APPLICATION_ID
FROM POSTING_APP_RESPONSE
WHERE ID = #{id}

SELECT NVL(MAX(ID), 0) AS COUNT
FROM POSTING_APP_RESPONSE
WHERE ID = #{id}
